function getCourses() {
    let xhr = new XMLHttpRequest();
    const url = "cons_cursos.php";
    xhr.open("GET", url, true);

    xhr.onreadystatechange = function() {
        if (xhr.readyState === 4) {
            if (xhr.status === 200) {
                let coursesVector = xhr.responseText.split('|');
                for (let i=0; i < coursesVector[0]; i++) {
                    let projectCode = coursesVector[2 + 3*i];
                    let courseName = coursesVector[3 + 3*i];
                    let courseCode = coursesVector[1 + 3*i];
                    console.log(projectCode,'-', courseName, '-', courseCode);
                }
            } else {
                console.error("Request failed with status:", xhr.status);
            }
        }
    };
    xhr.send();
}

getCourses();
